	private Context context = null;
	private DatabaseHelper db = null;
	
	private static final String TAG = "TestEncryption";
	private static final String userid1 = "1";
	private static final String userid2 = "2";
	private static final String nk = "_k:";
	private static final String em = "_e:";
	private static final String textToEncrypt = "Hello World. My name is Bob.";
	private ArrayList<Double> lengthList = null;
	private ArrayList<Double> lengthListAES = null;
	private ArrayList<Double> lengthListRSA = null;
	
	@Override
	protected Boolean doInBackground(AsyncTaskData... params) {
		String s = params[0].params[0];
			if(s.equals("time")){
				Log.d(TAG, "Key Exchange & Duration Test");
				context = params[0].context;
				db = DatabaseHelper.getInstance(context);
				db.deleteEncryption(userid1);
				db.deleteEncryption(userid2);
				db.insertEncryption(userid1);
				db.insertEncryption(userid2);
				long overallStart = System.currentTimeMillis();
				String m1 = firstKeyExchange();
				String m2 = secondKeyExchange(m1);
				String m3 = thridKeyExchange(m2);
				String m4 = fourthKeyExcahnge(m3);
				String m5 = msgEncryption(m4);
				msgDecryption(m5);
				outputDuration(overallStart, "Overall");
			} else if(s.equals("length")){
				Log.d(TAG, "Message Expansion Test");
				lengthList = new ArrayList<Double>();
				lengthListAES = new ArrayList<Double>();
				lengthListRSA = new ArrayList<Double>();
				testLength();
			} else if(s.equals("iv"))	{
				startAESIVTest();
			} else if (s.equals("keys")){
				startKeyTest();
			}
		return true;
	}
	
	//---------------------------------------------------------------------------------------
	//Key Exchange & Duration Test

	
	private void msgDecryption(String message) {
		long start = System.currentTimeMillis();
		String encrypted = message.substring(em.length());
		String[] keyValues = db.getKeyInfoUserIV(userid2);
		outputDuration(start,  "Database 6");
		long enc1 = System.currentTimeMillis();
		//Log.d(TAG, "Encrypted = " + encrypted);
		//Log.d(TAG, "Message = " + message);
		SecretKey key = KeyConversion.stringToSecretKey(keyValues[0]);
		String decrypted = EncryptDecrypt.decryptString(encrypted, key, keyValues[1]);
		if(decrypted.equals(textToEncrypt)){
			Log.d(TAG, "It Worked");
		} else {
			Log.d(TAG, "It Failed");
		}
		Log.d(TAG, decrypted);
		outputDuration(enc1, "Decryption 6");
	}

	private String msgEncryption(String message) {
		long start = System.currentTimeMillis();
		String key = message.substring(nk.length());
		//Log.d(TAG, "sub message = " + key);
		//Log.d(TAG, "Message = " + message);
		String[] keyValues = db.getKeyInfoUserIV(userid1);
		outputDuration(start,  "Database 5.1");
		long enc1 = System.currentTimeMillis();
		String decrypted = EncryptDecrypt.decryptString(key, KeyConversion.stringToSecretKey(keyValues[0]), keyValues[1]);
		//db.updateKey("keyCon", userid1, decrypted);
		outputDuration(enc1,  "Decryption 5");
		long dbStart = System.currentTimeMillis();
		ContentValues values = new ContentValues();
		values.put("keyCon", decrypted);
		updateKeyState(1, 1, 1, 1, userid1, values);
		outputDuration(dbStart,  "Database 5.2");
		long enc2 = System.currentTimeMillis();
		SecretKey ConKey = KeyConversion.stringToSecretKey(decrypted);
		String encrypted = EncryptDecrypt.encryptString(textToEncrypt, ConKey, keyValues[1]);
		String msg = em + encrypted;
		//Log.d(TAG, "Encrypted Message = " + msg);
		//Log.d(TAG, "Encrypted Message Length = " + Integer.toString(msg.length()));
		outputDuration(enc2, "Encryption 5");
		return msg;
	}

	private String fourthKeyExcahnge(String message) {
		long start = System.currentTimeMillis();
		String[] values = getStringValues(message);
		String key = values[0];
	//	Log.d(TAG, "4th sub message = " + key);
	//	Log.d(TAG, "4th Message = " + message);
		String aesKey = KeyConversion.secretKeyToString(KeyGeneration.getNewKeys());
		outputDuration(start,  "Key Creation 4");
		long dbstart = System.currentTimeMillis();
		String[] keyInfo = db.getKeyInfoPubPriv(userid2);
		outputDuration(dbstart,  "Database 4.1");
		long enc1 = System.currentTimeMillis();
		PrivateKey privateKey = KeyConversion.stringToPrivateKeyECC(keyInfo[1]);
		PublicKey publicKey = KeyConversion.stringToPublicKeyECC(keyInfo[0]);
		String dev = keyInfo[2];
		String enc = keyInfo[3];
		IEKeySpec keys = new IEKeySpec(privateKey, publicKey);
		String decrypted = EncryptDecrypt.decryptWithECC(keys, key, convertBytes(dev), convertBytes(enc));
		outputDuration(enc1,  "Decryption 4");
		ContentValues v = new ContentValues();
		v.put("keyCon", decrypted);
		v.put("keyUser", aesKey);
		v.put("ivVector", values[1]);
		//db.updateKeys(userid2, v);
		updateKeyState(1, 1, 1, 1, userid2, v);
		outputDuration(dbstart,  "Database 4.2");
		long enc2 = System.currentTimeMillis();
		SecretKey conKey = KeyConversion.stringToSecretKey(decrypted);
		String encrypted = EncryptDecrypt.encryptString(aesKey, conKey, values[1]);
		String msg = nk + encrypted;
		//Log.d(TAG, "Fourth Key = " + msg);
		//Log.d(TAG, "Fourth Key Length = " + Integer.toString(msg.length()));
		outputDuration(enc2, "Encryption 4");
		return msg;
	}

	private String thridKeyExchange(String message) {
		long start = System.currentTimeMillis();
		String[] values = getStringValues(message);
	//	Log.d(TAG, "sub message = " + values[0] + " ********** " + values[1]);
	//	Log.d(TAG, "Message = " + message);
		String aesKey = KeyConversion.secretKeyToString(KeyGeneration.getNewKeys());
		String iv = getBytes();
		outputDuration(start,  "Key Creation 3");
		long dbstart = System.currentTimeMillis();
		ContentValues v = new ContentValues();
		v.put("keyPub", values[0]);
		v.put("keyEnc", values[1]);
		v.put("keyUser", aesKey);
		v.put("ivVector", iv);
		//db.updateKeys(userid1, v);
		updateKeyState(1, 1, 1, 0, userid1, v);
		String[] keyInfo = db.getKeyInfoPriv(userid1);
		outputDuration(dbstart, "Database 3");
		long keyEnc = System.currentTimeMillis();
		PrivateKey privateKey = KeyConversion.stringToPrivateKeyECC(keyInfo[0]);
		String dev = keyInfo[1];
		PublicKey pkey = KeyConversion.stringToPublicKeyECC(values[0]);
		IEKeySpec keys = new IEKeySpec(privateKey, pkey);
		String encrypted = EncryptDecrypt.encryptWithECC(keys, aesKey, convertBytes(dev), convertBytes(values[1]));
		String msg = nk + encrypted + nk + iv; 
		//Log.d(TAG, "Third Key = " + msg);
		//Log.d(TAG, "Third Key Length = " + Integer.toString(msg.length()));
		outputDuration(keyEnc, "Encryption 3");
		return msg;
	}

	private String secondKeyExchange(String message) {
		long start = System.currentTimeMillis();
		String[] values = getStringValues(message);
		//Log.d(TAG, "sub message = " + values[0] + " ********** " + values[1]);
		//Log.d(TAG, "Message = " + message);
		KeyPair pair = KeyGeneration.getECCKeys();
		String privateKey = KeyConversion.privateKeyToString(pair.getPrivate());
		String publicKey = KeyConversion.publicKeyToString(pair.getPublic());
		outputDuration(start,  "Key Creation 2");
		long dbstart = System.currentTimeMillis();
		String bytes = getBytes();
		String msg = nk + publicKey + nk + bytes;
	//	Log.d(TAG, "Second Key = " + msg);
	//	Log.d(TAG, "Second Key Length = " + Integer.toString(msg.length()));
		ContentValues v = new ContentValues();
		v.put("keyPub", values[0]);
		v.put("keyDev", values[1]);
		v.put("keyPriv", privateKey);
		v.put("keyEnc", bytes);
		//db.updateKeys(userid2, v);
		updateKeyState(1, 1, 0, 0, userid2, v);
		outputDuration(dbstart, "Database 2");
		return msg;
	}

	private String firstKeyExchange() {
		long start = System.currentTimeMillis();
		KeyPair pair = KeyGeneration.getECCKeys();
		String privateKey = KeyConversion.privateKeyToString(pair.getPrivate());
		String publicKey = KeyConversion.publicKeyToString(pair.getPublic());
		outputDuration(start,  "Key Creation 1");
		long dbstart = System.currentTimeMillis();
		String bytes = getBytes();
		String msg = nk + publicKey + nk + bytes;
		//Log.d(TAG, "First Key = " + msg);
		//Log.d(TAG, "First Key Length = " + Integer.toString(msg.length()));
		db.updateKey("keyPriv", userid1, privateKey);
		ContentValues values = new ContentValues();
		values.put("keyPriv", privateKey);
		updateKeyState(1, 0, 0, 0, userid1, values);
		db.updateByteArrays(userid1, "keyDev", bytes);
		outputDuration(dbstart, "Database 1");
		return msg;
	}
	
	private String[] getStringValues(String message) {
		String[] values = message.split(nk);
		int length = values.length;
		return new String[]{values[length - 2], values[length - 1]};
	}

	private String getBytes() {
		SecureRandom random = new SecureRandom();
		byte[] b = new byte[16];
		random.nextBytes(b);
		return Base64.encodeToString(b, Base64.DEFAULT);
	}
	
	private byte[] convertBytes(String text){
		return Base64.decode(text, Base64.DEFAULT);
	}

	private void outputDuration(long start, String type){
		long end = System.currentTimeMillis();
		long ms = end - start;
		long min = ms / 60000;
		long s = (ms - (min * 60000)) / 1000; 
		long rms = ms - (s * 1000);
		String time = " time took: " + Long.toString(min) + " minutes, " + Long.toString(s) + " seconds, " + 
				Long.toString(rms) + " milliseconds.";
		Log.d(TAG, type + time);
	}
	
	private void updateKeyState(int one, int two, int three, int four, String id, ContentValues values){
		values.put("key1", one);
		values.put("key2", two);
		values.put("key3", three);
		values.put("key4", four);
		db.updatekeyState(values, id);
	}
	
	
