	private String[] createArray(String first, int i) {
		String s = "";
		for(int n = 1; n <= i; n++){
			Log.d(TAG, Integer.toString(n));
			s += first;
		}
		int t = s.length();
		int k = 13824499;
		Log.d(TAG, Integer.toString(t));
		if(s.length() == t && k < t)
			s = s.substring(0, k);
		return new String[]{s};
	}


	private void doForArray(PublicKey key, String[] list) {
		try{
			for(String s: list){
				Log.d(TAG, "Text = " + s);
				Log.d(TAG, "Length = " + Integer.toString(s.length()));
				String encrypted = EncryptDecrypt.encryptWithPublic(key, s.getBytes());
				Log.d(TAG, "Encrypted Text = " + encrypted);
				Log.d(TAG, "Encrypted Length = " + Integer.toString(encrypted.length()));
				int expansion = encrypted.length() - s.length();
				double ratio = (double) expansion / (double) s.length();
				lengthListRSA.add(ratio);
				Log.d(TAG, "Text Length increased by = " + Integer.toString(expansion) + " characters. By " + Double.toString(ratio) + " times");
				double msgNumberOriginal = (double) s.length() / (double) 160;
				double msgNumberEncrypted = (double) encrypted.length() / (double) 160;
				Log.d(TAG, "Unencrypted Message Number = " + Double.toString(msgNumberOriginal) +
						". Encrypted Message Number = " + Double.toString(msgNumberEncrypted));
			}			
		} catch(Exception ex){
			ex.printStackTrace();
		}
		
	}

	private void doForArray(SecretKey key, String[] list) {
		try{
			String iv = getBytes();
			for(String s: list){
				Log.d(TAG, "Text = " + s);
				Log.d(TAG, "Length = " + Integer.toString(s.length()));
				String encrypted = EncryptDecrypt.encryptString(s, key, iv);
				Log.d(TAG, "Encrypted Text = " + encrypted);
				Log.d(TAG, "Encrypted Length = " + Integer.toString(encrypted.length()));
				int expansion = encrypted.length() - s.length();
				double ratio = (double) expansion / (double) s.length();
				lengthListAES.add(ratio);
				Log.d(TAG, "Text Length increased by = " + Integer.toString(expansion) + " characters. By " + Double.toString(ratio) + " times");
				double msgNumberOriginal = (double) s.length() / (double) 160;
				double msgNumberEncrypted = (double) encrypted.length() / (double) 160;
				Log.d(TAG, "Unencrypted Message Number = " + Double.toString(msgNumberOriginal) +
						". Encrypted Message Number = " + Double.toString(msgNumberEncrypted));
			}			
		} catch(Exception ex){
			ex.printStackTrace();
		}
	}

	private void doForArray(String[] list) {
		KeyPair pair1 = KeyGeneration.getECCKeys();
		KeyPair pair2 = KeyGeneration.getECCKeys();
		IEKeySpec spec = new IEKeySpec(pair1.getPrivate(), pair2.getPublic());
		//IEKeySpec spec2 = new IEKeySpec(pair2.getPrivate(), pair1.getPublic());
				
		SecureRandom random = new SecureRandom();
		byte[]  d = new byte[16];
		byte[]  e = new byte[16];
		random.nextBytes(d);
		random.nextBytes(e);
		
		try{
			for(String s: list){
				Log.d(TAG, "Text = " + s);
				Log.d(TAG, "Length = " + Integer.toString(s.length()));
				String encrypted = EncryptDecrypt.encryptWithECC(spec, s, d, e);
				Log.d(TAG, "Encrypted Text = " + encrypted);
				Log.d(TAG, "Encrypted Length = " + Integer.toString(encrypted.length()));
				int expansion = encrypted.length() - s.length();
				double ratio = (double) expansion / (double) s.length();
				lengthList.add(ratio);
				Log.d(TAG, "Text Length increased by = " + Integer.toString(expansion) + " characters. By " + Double.toString(ratio) + " times");
				double msgNumberOriginal = (double) s.length() / (double) 160;
				double msgNumberEncrypted = (double) encrypted.length() / (double) 160;
				Log.d(TAG, "Unencrypted Message Number = " + Double.toString(msgNumberOriginal) +
						". Encrypted Message Number = " + Double.toString(msgNumberEncrypted));
			}			
		} catch(Exception ex){
			ex.printStackTrace();
		}		
	}
	
	private void outputAverageExpansion() {
		double expansion = 0;
		for(double d: lengthList){
			expansion += d;
		}
		expansion = expansion / (double) lengthList.size();
		Log.d(TAG, "Average ECIES Expansion was "  + expansion + " times the original size");
		
		expansion = 0;
		for(double d: lengthListAES){
			expansion += d;
		}
		expansion = expansion / (double) lengthListAES.size();
		Log.d(TAG, "Average AES Expansion was "  + expansion + " times the original size");
		
		expansion = 0;
		for(double d: lengthListRSA){
			expansion += d;
		}
		expansion = expansion / (double) lengthListRSA.size();
		Log.d(TAG, "Average RSA Expansion was "  + expansion + " times the original size");
	}
	
